import axios from "axios";
import React, { useEffect, useState } from "react";
import InputMask from 'react-input-mask';
import { Link, useLocation } from "react-router-dom";
import { Button, Container, Divider, Form, Icon } from 'semantic-ui-react';
import { ENDERECO_API } from "../util/constantes";

export default function FormProduto () {

	const [idProduto, setIdProduto] = useState();
const [codigo, setCodigo] = useState();
const [titulo, setTitulo] = useState();
const [descricao, setDescricao] = useState();
const [valorUnitario, setValorUnitario] = useState();
const [tempoEntregaMinimo, setTempoEntregaMinimo] = useState();
const [tempoEntregaMaximo, setTempoEntregaMaximo] = useState();


const { state } = useLocation();
   useEffect(() => {
	
	if (state != null && state.id != null) {
	axios.get(ENDERECO_API + "api/produto/" + state.id)
    .then((response) => {
	setIdProduto(response.data.idProduto)
    setCodigo(response.data.codigo)
	setTitulo(response.data.titulo)
    setDescricao(response.data.descricao)
	setValorUnitario(response.data.valorUnitario)
	setTempoEntregaMinimo(response.data.tempoEntregaMinimo)
	setTempoEntregaMaximo(response.data.tempoEntregaMaximo)
		})
	}
   }, [state])


	function salvar () {

		let produtoRequest = {

			nome:nome,
			cpf:cpf,
			dataNascimento:dataNascimento,
			foneCelular:foneCelular,
			foneFixo:foneFixo
		}

		if (idProduto != null) { //Alteração:
			axios.put(ENDERECO_API + "api/produto/" + idProduto, produtoRequest)
			.then((response) => { console.log('Produto alterado com sucesso.') })
			.catch((error) => { console.log('Erro ao alter um produto.') })
		} else { //Cadastro:
			axios.post(ENDERECO_API + "api/produto", produtoRequest)
			.then((response) => { console.log('Produto cadastrado com sucesso.') })
			.catch((error) => { console.log('Erro ao incluir o Produto.') })
		}
 
	}

	function formatarData (dataParam) {

		let data = new Date(dataParam);
		let dia = data.getDate() < 10 ? "0" + data.getDate() : data.getDate();
		let mes = (data.getMonth() + 1) < 10 ? "0" + (data.getMonth() + 1) : (data.getMonth() + 1);
		let dataFormatada = dia + "/" + mes + "/" + data.getFullYear();
	   
		return dataFormatada
	};

	

        return(
            <div>

                <div style={{marginTop: '3%'}}>

                    <Container textAlign='justified' >

					{ idProduto === undefined &&
                  <h2> <span style={{color: 'darkgray'}}> Produto &nbsp;<Icon name='angle double right' size="small" /> </span> Cadastro</h2>
                       }
                  { idCliente != undefined &&
                  <h2> <span style={{color: 'darkgray'}}> Produto &nbsp;<Icon name='angle double right' size="small" /> </span> Alteração</h2>
                    }


                        <Divider />

						<div style={{marginTop: '4%'}}>

							<Form>

								<Form.Group widths='equal'>

									<Form.Input
										required
										fluid
										label='Nome'
										maxLength="100"
										value={nome}
										onChange={e => setNome(e.target.value)}
									   placeholder ="Nome Completo"
									/>

									<Form.Input
										fluid
										label='CPF'>
										<InputMask 
										mask="999.999.999-99"
										value={cpf}
										onChange={e => setCpf(e.target.value)} 
										placeholder ="Informe seu CPF"
										/> 
									</Form.Input>

								</Form.Group>
								
								<Form.Group>

									<Form.Input
										fluid
										label='Fone Celular'
                                        width={6}>
										<InputMask 
										mask="(99) 9999.9999" 
										value={foneCelular}
										onChange={e => setFoneCelular(e.target.value)}
										placeholder ="Número de Celular" 
										/> 
									</Form.Input>

									<Form.Input
										fluid
										label='Fone Fixo'
                                        width={6}>
										<InputMask 
										mask="(99) 9999.9999" 
										value={foneFixo}
										onChange={e => setFoneFixo(e.target.value)} 
										placeholder ="Número Fixo" 
										/> 
									</Form.Input>

                                    <Form.Input
                                        fluid
                                        label='Data Nascimento'
                                        width={6}
                                    >
                                        <InputMask 
                                            mask="99/99/9999" 
											value={dataNascimento}
											onChange={e => setDataNascimento(e.target.value)} 
                                            maskChar={null}
                                            placeholder="Ex: 20/03/1985"
                                        /> 
                                    </Form.Input>

								</Form.Group>

								<Form.Group widths='equal' style={{marginTop: '4%'}}  className='form--empresa-salvar'>
									
								<Link to={'/list-produto'}>
									<Button
										type="button"
										inverted
										circular
										icon
										labelPosition='left'
										color='orange'>
										<Icon name='reply' />
										Voltar
									
									</Button>
									</Link>
									<Container textAlign='right'>
										
										<Button
											inverted
											circular
											icon
											labelPosition='left'
											color='blue'
											floated='right'
											onClick={() => salvar()}
										>
											<Icon name='save' />
											Salvar
										</Button>
										
									</Container>

								</Form.Group>

							</Form>
						</div>
                    </Container>
                </div>
			</div>
		)
	}
